/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-script-url */
import React, { Component } from "react";
import { connect } from "react-redux";
import _ from "lodash";
import FoodItem from "./food-items/foodItem.component";
import "./style.css";
import Modal from "./modals/MakeFoodOrder.component";
import Cart from "./cart/cart";

class App extends Component {
  state = {
    showForm: false,
    display: false,
    width: 250,
  };

  inputChange = (event) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  };

  addedFormDismissed = () => {
    this.setState({ showForm: false });
  };

  formSubmit = (event) => {
    const { mealValue } = this.state;
    const { username } = this.state;
    const { amount } = this.state;
    const { addOrder } = this.props;
    event.preventDefault();
    addOrder({ username: username, meal: mealValue, amount: amount });
    this.setState({
      mealValue: "",
      username: "",
      amount: "",
    });
  };

  renderToDo() {
    const { data } = this.props;
    const orders = _.map(data, (value, key) => {
      return <FoodItem key={key} orderId={key} order={value} />;
    });
    if (!_.isEmpty(orders)) {
      return orders;
    }
    return (
      <div className="col s10 offset-s1 center-align">
        <h4>No Food Request!</h4>
      </div>
    );
  }
  componentWillMount() {
    this.props.fetchOrders();
  }

  render() {
    const { showForm } = this.state;
    //   closeNav = () => {
    //       this.setState({
    //         width : 0
    //       });
    //   },
    //   openNav = () => {
    //     this.setState({
    //       width : 250
    //     });
    // };

    return (
      <React.Fragment>
        <div id="main" className="container-fluid">
          <nav>
            <div className="nav-wrapper black">
              <a href="#" className="brand-logo">
                Food Orders
              </a>
              <ul id="nav-mobile" className="right">
                <Cart />
              </ul>
            </div>
          </nav>
        </div>

        <div className="container-fluid">
          <div className="row"></div>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = ({ data }) => {
  return {
    data,
  };
};

export default connect(mapStateToProps, actions)(App);
